mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, demailaddr = #{divyAddr}, dlvy_request = #{divyRequest}, dlvy_date = #{divyDate}
WHERE tran_no = #{tranNo}

UPDATE 	transaction
SET 	tran_status_code = #{tranCode}
WHERE 	prod_no = #{purchaseProd.prodNo}

mapper.select:
  	SELECT	*
  	FROM (	SELECT	inner_table.* , ROWNUM AS row_seq
  			FROM (	SELECT	tran_no, prod_no, buyer_id, payment_option, 
 								receiver_name, receiver_phone, demailaddr, dlvy_request, 
 								tran_status_code, order_data, dlvy_date 
					FROM	transaction 
					WHERE 	buyer_id = #{buyerId}
					ORDER BY tran_no
				) inner_table
			WHERE	ROWNUM <= #{search.endRowNum} 
		)
	WHERE	row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}
